<?php if (!defined('THINK_PATH')) exit();?><?php if(is_array($riselist)): $i = 0; $__LIST__ = $riselist;if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$vo): ++$i;$mod = ($i % 2 )?><dl class="app_dl"><dt><a href="<?php echo U('AppStore://App-Apps/detail?aid=' . $vo['app_id']);?>" title="<?php echo ($vo['app_name']); ?>"><img alt="<?php echo ($vo['app_name']); ?>" src="<?php echo C('PICTURE_HOST');?><?php echo ($vo['AppDeviceType'][0]['icon']); ?>" width="80" height="80" /></a></dt><dt><a class="app_name" href="<?php echo U('AppStore://App-Apps/detail?aid=' . $vo['app_id']);?>" title="<?php echo ($vo['app_name']); ?>"><?php echo showCutStr($vo['app_name']);?></a></dt><dd class="app_author_name"><?php echo showCutStr($vo['author_name']);?></dd><dd><span class="blue_grade"><?php if($vo['app_starcount'] > 0): ?><i style="width:<?php echo round($vo['app_score_www']/$vo['app_starcount']) * 15;?>px;"></i><?php else: ?><i style="width:0px;"></i><?php endif; ?></span>(<?php echo number_format($vo['app_downloads']);?>)</dd></dl><?php endforeach; endif; else: echo "" ;endif; ?>